/*

  Given an array S of n integers, are there elements a, b, c in S such that a + b + c = 0?
  Find all unique triplets in the array which gives the sum of zero.

  Note: The solution set must not contain duplicate triplets.

  For example, given array S = [-1, 0, 1, 2, -1, -4],

  A solution set is:
  [
    [-1, 0, 1],
    [-1, -1, 2]
  ]

 */

// Things to note
// NOTE sum is always 0
// NOTE no duplicate tripples
// NOTE no duplicates to make the sum


function threeSum(nums, target = 0) {
  if(nums.length < 3) {
    return new Array();
  }

  const sortedNums = mergeSort(nums);
  const numLen = sortedNums.length;
  const ans = [];
  for(let i = 0; i < numLen; i++) {
    let left = i + 1;
    let right = numLen - 1;

    while(left < right) {

      const sum = sortedNums[i] + sortedNums[left] + sortedNums[right];

      if (sum === target) {
        ans.push([sortedNums[i], sortedNums[left], sortedNums[right]]);
        right--;
        left++;
        continue;
      }

      if(sum > 0) {
        right--;
      }

      if(sum < 0) {
        left++;
      }
    }
  }

  return ans;
}

function mergeSort(list) {
  // if the list is of length 1, it is alredy sorted!
  if (list.length === 1) {
    return list
  }

  const middle = Math.round(list.length / 2);
  const left = list.slice(0, middle);
  const right = list.slice(middle, list.length);

  return merge(mergeSort(left), mergeSort(right));
}

/**
 * Merges sorted lists into a biggest sorted list
 * @param  {Array} left  ordered lisr
 * @param  {Array} right ordered lisr
 * @return {Array}       joined ordered list
 */
function merge(left, right) {
  const sortedList = [];

  while(left.length && right.length) {
    if (left[0] > right[0]) {
      sortedList.push(right.shift());
      continue;
    }

    sortedList.push(left.shift());
  }

  if(left.length) {
    sortedList.push(...left);
  }

  if(right.length) {
    sortedList.push(...right);
  }

  return sortedList;
}

